import React, { useState } from "react";
import { doctors } from "../tools/Doctors";

export default function DoctorsCard() {
    const [activeIndex, setActiveIndex] = useState(null);
    

    return (
        <div className="max-w-full overflow-x-auto py-12 px-6">
            {/* Title Section */}
            <div className="text-center mb-12">
                <h1 className="text-[#444444] text-4xl font-['Gilroy-Medium']">
                    Our Best Doctors
                </h1>
                <p className="text-[#878787] text-lg font-['Helvetica'] mt-2">
                    Beyond genius really enough passed is up.
                </p>
            </div>

            {/* Doctor Cards Section */}
            <div className="flex space-x-8 pb-8">
                {doctors.map((doctor, index) => (
                    <div
                        key={index}
                        className="flex-none bg-white shadow-lg rounded-lg flex flex-col items-center p-4 w-[300px] sm:w-[350px] lg:w-[400px]"
                    >
                        <img
                            className="w-full h-[300px] object-cover rounded-md"
                            src=""
                            alt={doctor.name}
                        />
                        <div
                            key={index}
                            className={`snap-always bg-white shadow-lg rounded-lg flex flex-col items-center p-4 
                                transition-all duration-300 ${activeIndex === index ? "scale-105" : "scale-100"
                                }`}
                            onMouseEnter={() => setActiveIndex(index)}
                            onMouseLeave={() => setActiveIndex(null)}
                        />
                        <div className="mt-6 text-center">
                            <h3 className="text-[#444444] text-2xl font-['Gilroy-SemiBold']">
                                {doctor.name}
                            </h3>
                            <p className="text-[#878787] text-lg font-['Helvetica'] mt-1">
                                ({doctor.title})
                            </p>
                            <p className="text-[#878787] text-lg font-['Helvetica'] mt-4">
                                {doctor.description}
                            </p>
                        </div>
                        <div className="mt-6 flex gap-4">
                            <button className="border border-[#878787] rounded-md shadow px-4 py-2 text-sm text-[#878787] font-['Gilroy-SemiBold']">
                                View Details
                            </button>
                            <button
                                className={`rounded-md shadow px-6 py-2 text-sm font-['Gilroy-SemiBold'] ${activeIndex === index 
                                    ? "bg-[#0594ff] text-white"
                                    : "bg-gray-300 text-gray-700"
                                    }`}
                                    onMouseEnter={() => setActiveIndex(index)}
                                    onMouseLeave={() => setActiveIndex(null)}

                            >
                                Schedule Appointment
                            </button>
                        </div>
                    </div>
                ))}
            </div>

            {/* Pagination Dots */}
            <div className="flex items-center justify-center gap-2 mt-8 fixed top-[740px] left-[720px] transform -translate-x-1/2 -translate-y-1/2 z-10">
                {[...Array(5)].map((_, index) => (
                    <span
                        key={index}
                        className={`w-4 h-4 rounded-full ${index === 0 ? "bg-[#0594ff]" : "bg-[#ffecec]"
                            }`}
                    />
                ))}
            </div>

        </div>

    );
}
