import React, { useState, useRef, useEffect } from 'react';

export default function DoctorsCard() {
  const [activeIndex, setActiveIndex] = useState(0);
  const scrollContainerRef = useRef(null);

  const doctors = [
    {
      name: "Dr. Shaun",
      title: "Surgeon",
      description: "And excellence partiality estimating terminated day everything.",
    },
    {
      name: "Dr. Andrew",
      title: "Surgeon",
      description: "Excellence partiality estimating terminated day everything.",
    },
    {
      name: "Dr. Brown",
      title: "Oncologist",
      description: "Beyond genius really enough passed is up.",
    },
    {
        name: "Dr. Brown",
        title: "Oncologist",
        description: "Beyond genius really enough passed is up.",
      },
      {
        name: "Dr. Brown",
        title: "Oncologist",
        description: "Beyond genius really enough passed is up.",
      },
    // Add more doctors if necessary
  ];

  // Function to handle scroll and update activeIndex
  const handleScroll = () => {
    if (scrollContainerRef.current) {
      const container = scrollContainerRef.current;
      const children = container.children;
      const offset = container.scrollLeft;
      const containerWidth = container.offsetWidth;

      let newIndex = 0;
      // Loop through the doctor cards to find which one is most visible in the center
      Array.from(children).forEach((child, index) => {
        const childOffsetLeft = child.offsetLeft;
        if (
          childOffsetLeft >= offset - containerWidth / 2 &&
          childOffsetLeft <= offset + containerWidth / 2
        ) {
          newIndex = index;
        }
      });

      setActiveIndex(newIndex);
    }
  };

  useEffect(() => {
    const container = scrollContainerRef.current;
    if (container) {
      container.addEventListener('scroll', handleScroll);
    }

    return () => {
      if (container) {
        container.removeEventListener('scroll', handleScroll);
      }
    };
  }, []);

  return (
    <div className="max-w-[1440px] mx-auto mt-8">
      <div className="relative">
        {/* Scrollable Container */}
        <div
          ref={scrollContainerRef}
          className="overflow-x-auto flex gap-6 py-4 scroll-smooth"
        >
          {doctors.map((doctor, index) => (
            <div
              key={index}
              className="snap-always bg-white shadow-lg rounded-lg flex flex-col items-center p-4"
            >
              <img
                className="w-full h-[300px] object-cover rounded-md"
                src="https://via.placeholder.com/437x383"
                alt={doctor.name}
              />
              <div
                className={`w-full h-1 mt-4 ${
                  index === activeIndex ? "bg-[#0594ff]" : "bg-[#dcdcdc]"
                }`}
              />
              <div className="mt-6 text-center">
                <h3 className="text-[#444444] text-2xl font-['Gilroy-SemiBold']">
                  {doctor.name}
                </h3>
                <p className="text-[#878787] text-lg font-['Helvetica'] mt-1">
                  ({doctor.title})
                </p>
                <p className="text-[#878787] text-lg font-['Helvetica'] mt-4">
                  {doctor.description}
                </p>
              </div>
              <div className="mt-6 flex gap-4">
                <button className="border border-[#878787] rounded-md shadow px-4 py-2 text-sm text-[#878787] font-['Gilroy-SemiBold']">
                  View Details
                </button>
                <button
                  className={`rounded-md shadow px-6 py-2 text-sm font-['Gilroy-SemiBold'] ${
                    index === activeIndex
                      ? "bg-[#0594ff] text-white"
                      : "bg-gray-300 text-gray-700"
                  }`}
                >
                  Schedule Appointment
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* Scroll Dots for navigation */}
        <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex gap-2">
          {[...Array(doctors.length)].map((_, index) => (
            <span
              key={index}
              className={`w-4 h-4 rounded-full ${
                activeIndex === index ? "bg-[#0594ff]" : "bg-[#ffecec]"
              }`}
            />
          ))}
        </div>
      </div>
    </div>
  );
}
