import React from 'react'
import { Cards } from '../tools/CardsWork'

export default function HowItWork() {
  return (
    <div className="container mx-auto w-[1146px] h-[455px] relative">
  <h1 className="title text-center text-[#444444] text-[50px] font-medium leading-[60px]">How it Works</h1>
  <p className="subtitle text-center text-[#878787] text-lg leading-[21px] mt-[10px]">
    We have been providing great flooring solutions service.
  </p>
  <div className="steps flex justify-between mt-[50px]">
    {Cards.map((card, index) => (
      <div key={index} className="step-card relative w-[264px] h-[264px] bg-white shadow">
        <div className={`underline absolute bottom-0 w-full h-1 ${index === 0 ? 'bg-[#0594ff]' : 'bg-[#dcdcdc]'}`}/>
        <div className="step-number absolute left-8 top-8 text-[#444444] text-[100px] font-extrabold opacity-5 leading-[130px]">
          {card.step}
        </div>
        <h2 className="step-title absolute left-8 top-[84px] text-[#444444] text-[28px] font-semibold leading-9">
          {card.title}
        </h2>
        <p className="step-description absolute left-8 top-[168px] text-[#878787] text-lg leading-[21px]">
          {card.description}
        </p>
      </div>
    ))}
  </div>
</div>

  )
}
